sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	response.setContentType("text/html");

	var writer = response.getStreamWriter();
	var session = gs.getSession();
	var loggedIn = session.isLoggedIn();
	var token = session.getSessionToken();

	response.setHeader('X-Is-Logged-In',loggedIn);
	if(!loggedIn){
		response.setStatus(302);
		response.setHeader('Location','/session_timeout.do');
	} else {
		response.setStatus(200);
		var gr = new GlideRecord("x_47399_yuiver_endpoint");

		if(gr.get("endpoint",request.pathParams.endpoint)){
			var data_html = gr.getValue("data");
			var auth_html = '<script>window.servicenowUserToken="' + token + '";</script></head>';

			data_html = data_html.replace('</head>',auth_html);
			writer.writeString(data_html);
		} else{
			writer.writeString("<html><head><body><p><b>Thank you for your response from "+ request.pathParams.endpoint + ".</b></p>"+"Logged in: "+loggedIn + ". token: "+token +"</body></head></html>");
		}
	}




})(request, response);
